import React, { Component } from 'react'
import { Text, View, ImageBackground, StyleSheet, FlatList, TouchableOpacity, Platform } from 'react-native'

import commonStyles from './estilosComuns'
import todayImage from './assets/imgs/today.jpg'

import Icon from 'react-native-vector-icons/FontAwesome'

import moment from 'moment'
import 'moment/locale/pt-br'

import Task from './components/Tarefa'
import AddTask from './AdicionarTarefa'
import estilosComuns from './estilosComuns'

export default class App extends Component {
    state = {
        mostrarTarefasConcluidas: true,
        mostrarAdicionarTarefa: false,
        tarefasVisiveis: [],
        tarefas: [{
            id: Math.random(),
            descricao: 'Comprar Livro de React Native',
            estimar: new Date(),
            concluir: new Date()
        }, {
            id: Math.random(),
            descricao: 'Ler Livro de React Native',
            estimar: new Date(),
            concluir: null
        }]
    }

    componentDidMount = () => {
        this.filtrarTarefas()
    }

    botaoMostarTarefasConcluidas = () => {
        this.setState({ mostrarTarefasConcluidas: !this.state.mostrarTarefasConcluidas }, this.filtrarTarefas)
    }

    filtrarTarefas = () => {
        let tarefasVisiveis = null
        if(this.state.mostrarTarefasConcluidas) {
            tarefasVisiveis = [...this.state.tarefas]
        } else {
            const pendente = function(tarefa) {
                return tarefa.concluir === null
            }
            tarefasVisiveis = this.state.tarefas.filter(pendente)
        }

        this.setState({ tarefasVisiveis })
    }

    botaoTarefa = tarefaId => {
        const tarefas = [...this.state.tarefas]
        tarefas.forEach(tarefa => {
            if(tarefa.id === tarefaId) {
                tarefa.concluir = tarefa.concluir ? null : new Date()
            }
        })

        this.setState({ tarefas }, this.filtrarTarefas)
    }

    render() {
        const today = moment().locale('pt-br').format('dddd, D [de] MMMM')
        return (
            <View style={styles.container}>
                <AddTask visivel={this.state.mostrarAdicionarTarefa} onCancel={() => this.setState({ mostrarAdicionarTarefa: false })} />
                <ImageBackground source={todayImage} style={styles.background}>
                    <View style={styles.iconBar}>
                        <TouchableOpacity onPress={this.botaoMostarTarefasConcluidas}>
                            <Icon name={this.state.mostrarTarefasConcluidas ? 'eye' : 'eye-slash'} size={20} color={estilosComuns.cores.secundario} />
                        </TouchableOpacity>
                    </View>
                    <View style={styles.titleBar}>
                        <Text style={styles.title}>Hoje</Text>
                        <Text style={styles.subtitle}>{today}</Text>
                    </View>
                </ImageBackground>
                <View style={styles.taskList}>
                    <FlatList data={this.state.tarefasVisiveis} keyExtractor={item => `${item.id}`} renderItem={({item}) => <Task {...item} botaoTarefa={this.botaoTarefa} />} />
                </View>
                <TouchableOpacity style={styles.addButton} activeOpacity={0.7} onPress={() => this.setState({ mostrarAdicionarTarefa: true })}>
                    <Icon name="plus" size={20} color={estilosComuns.cores.secundario} />
                </TouchableOpacity>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    background: {
        flex: 3
    },
    taskList: {
        flex: 7
    },
    titleBar: {
        flex: 1,
        justifyContent: 'flex-end'
    },
    title: {
        color: commonStyles.cores.secundario,
        fontSize: 50,
        marginLeft: 20,
        marginBottom: 20
    },
    subtitle: {
        color: estilosComuns.cores.secundario,
        fontSize: 20,
        marginLeft: 20,
        marginBottom: 20
    },
    iconBar: {
        flexDirection: 'row',
        marginHorizontal: 20,
        justifyContent: 'flex-end',
        marginTop: Platform.OS === 'ios' ? 40 : 10
    },
    addButton: {
        position: 'absolute',
        right: 30,
        bottom: 30,
        width: 50,
        height: 50,
        borderRadius: 25,
        backgroundColor: estilosComuns.cores.hoje,
        justifyContent: 'center',
        alignItems: 'center'
    }
});
